//             <h2>{note.content}</h2>
//             <div>{note.user}</div>
//             <div><strong>{note.important ? 'important' : ''}</strong></div>
//         </div>
//     )
// }

//below the version using useMatch():
// const Note = ({note}) => {
//     ;<div>
//         <h2>{note.content}</h2>
//         <div>{note.user}</div>
//         <div>
//             <strong>{note.important ? 'important' : ''}</strong>
//         </div>
//     </div>
// }
const Note = ({note}) => {
    return (
        <div>
            <h2>{note.content}</h2>
            <div>{note.user}</div>
            <div>
                <strong>{note.important ? 'important' : ''}</strong>
            </div>
        </div>
    )
}

// //clicking the name of a note whose id is 3 would trigger an event that changes the address of the browser into notes/3:
// const Notes = ({notes}) => (
//     <div>
//         <h2>Notes</h2>
//         <Table striped>
//             <ul>
//                 {notes.map((note) => (
//                     <li key={note.id}>
//                         {' '}
//                         <Link to={`/notes/${note.id}`}>
//                             {note.content}
//                         </Link>{' '}
//                     </li>
//                 ))}
//             </ul>
//         </Table>
//     </div>
// )
//below are the update version using react-boostrap:
const Notes = ({notes}) => (
    <div>
        <h2>Notes</h2>
        <TableContainer component={Paper}>
            <Table striped>
                <tbody>
                    {notes.map((note) => (
                        <tr key={note.id}>
                            <td>
                                <Link to={`/notes/${note.id}`}>
                                    {note.content}
                                </Link>
                            </td>
                            <td>{note.user}</td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </TableContainer>
    </div>
)

const Users = () => (
    <div>
        <h2>TKTL notes app</h2>
        <ul>
            <li>Matti Luukkainen</li>
            <li>Juha Tauriainen</li>
            <li>Arto Hellas</li>
        </ul>
    </div>
)

const Login = (props) => {
    const navigate = useNavigate()

    const onSubmit = (event) => {
        event.preventDefault()
        props.onLogin('mluukkai')
        // navigate to the Home page
        navigate('/')
    }

    return (
        // <div>
        //     <h2>login</h2>
        //     <form onSubmit={onSubmit}>
        //         <div>
        //             username: <input />
        //         </div>
        //         <div>
        //             password: <input type="password" />
        //         </div>
        //         <button type="submit">login</button>
        //     </form>
        // </div>
        //below is the update format using Form from react-bootstrap: https://react-bootstrap.netlify.app/forms/overview/#rb-docs-content
        <div>
            <h2>login</h2>
            <Form onSubmit={onSubmit}>
                <Form.Group>
                    <Form.Label>username:</Form.Label>
                    <Form.Control type="text" name="username" />
